import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {retrieveRAMS} from '../actions/actions';
import { Button } from 'react-bootstrap';
class RAM extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
        this.CPU = "";
        this.GPU = "";
        this.name = "";
        this.pricesum = 0;
      }
      componentDidMount(){
          this.fetchRAMS();
    }
    fetchRAMS = () => {
        retrieveRAMS().then(res =>{ 
            this.setState({data:res});
        }).catch(err => {
            alert('Failed to fetch RAMS! '+err);
        });
      }
      selectRAM = item => event => {
          event.preventDefault();
          const {name} = this.props.location.state;
          const {CPU} = this.props.location.state;
          const {GPU} = this.props.location.state;
          let RAM = item.RAM_Name+" "+item.RAM_Storage;
          const {pricesum} = this.props.location.state;
          let pricesum2 = parseFloat(item.Price) + parseFloat(pricesum);
          alert("RAM Selected: "+item.RAM_Name+" "+item.RAM_Storage);
          this.props.history.push("/storage",{
              name:name,
              CPU:CPU,
              GPU:GPU,
              RAM:RAM,
              Price:item.Price,
              pricesum2:pricesum2
          });
      }
      render() {
        const {CPU} = this.props.location.state;
        const {GPU} = this.props.location.state;
        const {Price} = this.props.location.state;
        let rows = [];
        const {name} = this.props.location.state;
        const {pricesum2} = this.props.location.state;
        this.pricesum = pricesum;
        this.name = name;
        this.CPU = CPU;
        this.GPU = GPU;
        let pricesummation = pricesum2.toFixed(2) - Price.toFixed(2);
        console.log(pricesummation);
        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.RAM_ID}</td>
            <td>{item.RAM_Name}</td>
            <td>{item.RAM_Storage}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectRAM(item)}>Select RAM</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
            <td>{item.RAM_ID}</td>
            <td>{item.RAM_Name}</td>
            <td>{item.RAM_Storage}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectRAM(item)}>Select RAM</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a RAM Option
            </h2>
            <br></br>
            <Link className="link" to={{
                pathname:'/gpu',
                state: {name: this.name,pricesum:pricesummation,CPU:this.CPU,GPU:this.GPU}
            }}> Back to GPU Page </Link>
            <br></br>
              </div>
              <br></br>
            <div className="form-group" id="over">
            <img src="ram.png" ></img>
            </div>
            <br></br>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>RAM NB</th>
                <th>RAM NAME</th>
                <th>RAM STORAGE</th>
                <th>PRICE $</th>
                <th>RAM SELECTION</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
      }
  }
export default RAM;
