import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {retrievePSUS} from '../actions/actions';
import { Button } from 'react-bootstrap';
class PSU extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
      }
      componentDidMount(){
          this.fetchPSUS();
    }
    fetchPSUS = () => {
        retrievePSUS().then(res =>{ 
            this.setState({data:res});
        }).catch(err => {
            alert('Failed to fetch PSUS! '+err);
        });
      }
      selectPSU = item => event => {
          event.preventDefault();
        const {pricesum4} = this.props.location.state;
        let totalprice = parseFloat(item.Price) + parseFloat(pricesum4);
        alert(totalprice);
          this.props.history.push("/totalprice",{
              Pow_ID: item.Pow_ID,
              Pow_Name: item.Pow_Name,
              Pow_Capacity: item.Pow_Capacity,
              Price: item.Price,
              totalprice: totalprice
          });
      }
      render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.Pow_ID}</td>
            <td>{item.Pow_Name}</td>
            <td>{item.Pow_Capacity}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectPSU(item)}>Select PSU</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
            <td>{item.Pow_ID}</td>
            <td>{item.Pow_Name}</td>
            <td>{item.Pow_Capacity}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectPSU(item)}>Select PSU</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a PSU Option
            </h2>
            <br></br>
            <Link className="link" to="/motherboard"> Back to Motherboard Page </Link>
            <br></br>
              </div>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>PSU NB</th>
                <th>PSU NAME</th>
                <th>PSU CAPACITY</th>
                <th>PRICE $</th>
                <th>PSU SELECTION</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
      }
  }
export default PSU;
