import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {deleteOrder,retrieveCustomerOrders} from '../actions/actions';
import { Button } from 'react-bootstrap';
class PCOrderInformation extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
        this.price = 0;
        this.CPU = "";
        this.GPU = "";
        this.name = "";
        this.RAM = "";
        this.Storage = "";
        this.Motherboard = "";
        this.PSU = "";
      }
      componentDidMount(){
        this.fetchCustomerOrders();
    }
    fetchCustomerOrders = () => {
      retrieveCustomerOrders().then(res =>{ 
        this.setState({data:res});
    }).catch(err => {
        alert('Failed to fetch orders! '+err);
    });
    }
    DeleteOrder = (event) =>{
      event.preventDefault();
      const {CPU} = this.props.location.state;
      const {name} = this.props.location.state;
      deleteOrder({data: CPU})
      .then(response => {
        this.fetchCustomerOrders();
          this.props.history.push("/orderinformation",{
            name: name
          });
          alert('Succesfully Deleted Order!');

      }).catch(err => {
          alert('Order failed to delete !' +err);
      })
    }
      render() {
        const {totalprice} = this.props.location.state;
        const {name} = this.props.location.state;
        const {CPU} = this.props.location.state;
        const {GPU} = this.props.location.state;
        const {RAM} = this.props.location.state;
        const {Storage} = this.props.location.state;
        const {Motherboard} = this.props.location.state;
        const {PSU} = this.props.location.state;
        this.name = name;
        this.CPU = CPU;
        this.GPU = GPU;
        this.RAM = RAM;
        this.Storage = Storage;
        this.Motherboard = Motherboard;
        this.PSU = PSU;
        this.price = totalprice.toFixed(2);
        return (
            <div className="row mt-5">
            <div className="col-lg-12 m-auto">
              <div className="card card-body">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i>Customer Order Information
              </h2>
              <br></br>
              <Link className="link" to={{
                  pathname:'/customerorders',
                  state:{name: this.name}
              }}> Back to Customer Orders </Link>
              <br></br>
              <div className="form-group" id="over">
              <img src="greengamingpc.png" ></img>
              </div>
              <div className="form-group">
              <h3 class="text-center mb-3" className="Label"> <b> PC Order Information: 

              </b> 
              </h3> 
              <br></br>
              <h4 class="text-center mb-3" className="Label"> <b> CPU: {this.CPU}

              </b> 
              </h4> 
              <br></br>
              <h4 class="text-center mb-3" className="Label"> <b> GPU: {this.GPU}

              </b> 
              </h4> 
              <br></br>
              <h4 class="text-center mb-3" className="Label"> <b> RAM: {this.RAM}

              </b> 
              </h4> 
              <br></br>
              <h4 class="text-center mb-3" className="Label"> <b> Storage: {this.Storage}

              </b> 
              </h4> 
              <br></br>
              <h4 class="text-center mb-3" className="Label"> <b> Motherboard: {this.Motherboard}

              </b> 
              </h4>
              <br></br> 
              <h4 class="text-center mb-3" className="Label"> <b> PSU: {this.PSU}

              </b> 
              </h4> 
              <br></br>
              <h4 class="text-center mb-3" className="Label"> <b> Total Price: $$ {this.price}

              </b> 
              </h4> 
              <br></br>
              <br></br>
              <div className="form-group">
                <button type="submit" onClick={this.DeleteOrder} className="btn">
                Delete Order
               </button>
               <br></br>
               </div>
                <br></br>

                <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
              NDXHardwareâ„¢</b> 
              </h5>
              <br></br>
              </div>

              </div> 
            </div>
          </div>
        );
      }
  }
export default PCOrderInformation;
