import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import { updateUser } from '../actions/actions';



class UserForm extends Component {

  constructor(props){
    super(props);
    // This binding is necessary to make `this` work in the callback
    this.UserRegistrationFunction = this.UserRegistrationFunction.bind(this);
    this.state = {user_name: '', user_password: '', user_age: '',user_address: '', user_job: ''}
  }

  componentDidMount(){

  }

  handleNameChange = event => {
    event.preventDefault();
    this.setState({user_name: event.target.value});
  }
  handlePasswordChange = event =>{
      event.preventDefault();
      this.setState({user_password: event.target.value});
  }

  handleAgeChange = event => {
    event.preventDefault();
    this.setState({user_age: event.target.value});
  }

  handleAddress = event => {
    event.preventDefault();
    this.setState({user_address: event.target.value});
  }

  handleJob = event => {
    event.preventDefault();
    this.setState({user_job: event.target.value});
  }

  UserRegistrationFunction = (event) => {

    event.preventDefault();
    const{user_name} = this.state;
    const{user_password} = this.state;
    const{user_age} = this.state;
    const{user_address} = this.state;
    const{user_job} = this.state;
    let account_id = this.props.location.state.account_id;   
     updateUser({ account_id:account_id,
        user_name:user_name,
        user_password:user_password,
        user_age:user_age,
        user_address:user_address,
        user_job:user_job,}).then(res => {
        this.props.history.push('/');
        alert('Account Successfully Updated');
    }).catch(err =>{
        alert('Account failed to update! '+err);
    });
  }
  render() {

    return (
      <div className="App">
        <h1>User Account Form</h1>
        <Link to="/"> Go back to Table </Link>
        <form onSubmit={this.UserRegistrationFunction} method="POST" className="">
          <div className="form-group row">
            <label className="form-label" for="user_name">Name:</label>
            <input className="form-control"  type="text" onChange={this.handleNameChange} id="user_name" name="name" placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_name : 'Name'} required  size="10"/><br/>
          </div>
          <div className="form-group row">
            <label className="form-label" for="user_password">Password:</label>
            <input className="form-control" type="text" id="user_password" name="password" onChange={this.handlePasswordChange} placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_password : 'Password'} required  size="10"/><br/>
          </div>
          <div className="form-group row">
            <label className="form-label" for="user_age">Age:</label>
            <input className="form-control" type="text" id="user_age" name="age" onChange={this.handleAgeChange} placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_age : 'Age'} required  size="10"/><br/>
          </div>
        <div className="form-group row">
          <label className="form-label" for="user_address">Address:</label>
          <input className="form-control" type="text" id="user_address" name="address" placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_address : 'Address'} onChange={this.handleAddress} required  size="10"/><br/>
        </div>
        <div className="form-group row">
          <label className="form-label" for="user_job">Job:</label>
          <input className="form-control" type="text" id="user_job" name="job" placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_job : 'Hobby'} required size="10" onChange={this.handleJob}/><br/>
        </div>
          <input className="btn bg-primary text-light" type="submit" value="Save" />
        </form>
      </div>
    );
  }
}

export default UserForm;
