import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {findUser,retrieveUsers} from '../actions/actions';
import { Button, Navbar, Nav, NavItem, NavDropdown, MenuItem } from 'react-bootstrap';
import { toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css';
class MainMenu extends Component {
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.name = "";
      }

      componentDidMount(){
      toast.success("Your epic PC Customization journey now begins! ðŸ˜Ž")
    }

  gotoCustomPC = (event) => {
     event.preventDefault();
     const {name} = this.props.location.state;
     this.props.history.push("/custompccpu",{
         name:name
     });
  }
  gotoBudgetPC = (event) => {
    event.preventDefault();
    const {name} = this.props.location.state;
    this.props.history.push("/budgetpc",{
        name:name
    });
  }
  gotoViewOrders = (event) => {
    event.preventDefault();
    const {name} = this.props.location.state;
    this.props.history.push("/customerorders",{
        name:name
    });
  }
  Logout = (event) => {
    event.preventDefault();
    const {name} = this.props.location.state;
    this.props.history.push("/login",{
        name:name
    });
  }
    render() {
        const {startprice} = this.props.location.state;
        const {name} = this.props.location.state;
        this.name = name;
        console.log(startprice);
      return (

        <div className="container">
          <Navbar fixed="top" className="color-nav" collapseOnSelect expand="lg" variant="dark" >
          <Navbar.Brand href="/home">
      <img
        alt=""
        src="/greengamingpc.png"
        width="25"
        height="33"
        className="d-inline-block align-top"
      />{' '}
     NDXHardwareâ„¢
    </Navbar.Brand>
  <Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <Navbar.Collapse id="responsive-navbar-nav">
    <Nav className="mr-auto">
      <Nav.Link  onClick={this.gotoViewOrders}>View Orders</Nav.Link>
    </Nav>
    <Nav>
      <Nav.Link onClick={this.Logout} >
        Logout
      </Nav.Link>
    </Nav>
  </Navbar.Collapse>
</Navbar>
          <div className="col-lg-12 m-auto">

            <div className="card card-body">
              <br></br>
              <br></br>
              <br></br>
              <br></br>
            <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i>Welcome to NDXHardwareâ„¢
            </h2>
            <br></br>
            <br></br>
            <div className="form-group" id="over">
            <img src="home.png" ></img>
            </div>
            <br></br>
            <br></br>
            <br></br>
            <div className="form-group">
            <h4 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> <b>
            If there's no hardware for a gamer
            then theres no need for a player.
            </b> 
            </h4> 
            </div>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <div className="form-group">
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            Please select an option to satisfy your need for PC Gaming ðŸ˜Ž:</b> 
            </h5>  
            <br></br>

            </div>
            <br></br>
        <br></br>
            <div className="form-group">
               <br></br>
               <br></br>
               <div className="form-group">
               <img class="budgetpc" src="budgetpcoption.png"></img>
        <h3 class="text-center mb-3" className="text">Budget PC</h3>
        <p className="text">You are a gamer that has a certain budget, yet you dont known anything about the parts of the computers, then this option is for you. Very simple, you choose a budget and it will display a range of PC Configurations based
          on that budget.
        </p>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>

        <img class="custompc" src="custompcoption.png"></img>
        <br></br>
        <h3>C++ Application Development</h3>
        <p>Moreover, I have also developed software applications using C++. Through the use of C++, I have learned to develop software applications using data structures such as stacks, queues, linked lists and binary search trees. </p>
               </div>
               <br></br>
                <div className="form-group">
                <button type="submit" onClick={this.gotoBudgetPC} className="btn">
                 Budget PC
               </button>
                </div>
                <div className="form-group">
                <button type="submit" onClick={this.gotoCustomPC} className="btn">
                 Custom PC
               </button>
                </div>
            </div>
              <br></br>
              <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardwareâ„¢</b> 
            </h5>
            </div> 
        </div>

        </div>
      );
    }
  }
  export default MainMenu;
