import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {findUser,retrieveUsers} from '../actions/actions';
import { Button } from 'react-bootstrap';
class Login extends Component {
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.UserAuthenticationFunction = this.UserAuthenticationFunction.bind(this);
        this.state = {name: '', password: '',data: []}

      }
      componentDidMount(){
        this.fetchUsers();
    }
    fetchUsers = () => {
      retrieveUsers().then(res =>{ 
          this.setState({data:res});
      }).catch(err => {
          alert('Failed to fetch users! '+err);
      });
    }

    handleNameChange = (event) => {
      event.preventDefault();
      this.setState({name: event.target.value});
    }
    handlePasswordChange = (event) =>{
        event.preventDefault();
        this.setState({password: event.target.value});
    }

    UserAuthenticationFunction = (event) => {
      event.preventDefault();
      let authenticate = false;
      const {name} = this.state;
      const {password} = this.state;

      const contents = this.state.data.forEach((item,key) => {
        if(item.password === password && item.name === name){
          authenticate = true;
          this.props.history.push("/Table1");
        }
      });
     if(authenticate === false){
       alert("Incorrect Username/Password! Please try again!")
     } 

    };
    render() {

      return (
        <div className="row mt-5 bg-dark">
          <div className="col-md-6 m-auto">
            <div className="card card-body">
            <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> NDX Hardware
            </h2>
            <div className="form-group" id="over">
            <img src="greengamingpc.png" ></img>
            </div>
            <div className="form-group">   
            <label className="Label" for="name">Name:</label>
            <input class="form-control"  type="name" onChange={this.handleNameChange} id="name" name="name" placeholder={(this.props.location.state !== undefined) ? this.props.location.state.name : 'Name'} required  size="10"/><br/>
            </div>
            <div className="form-group">
              <label className="Label" for="password">Password:</label>
              <input className="form-control" type="text" id="password" name="password" onChange={this.handlePasswordChange} placeholder={(this.props.location.state !== undefined) ? this.props.location.state.password : 'Password'} required  size="10"/><br/>
            </div>
            <button type="submit" onClick={this.UserAuthenticationFunction} className="btn">
              Login
            </button>
            <br></br>
            <div className="form-group">
            <Link className="link"  to="/registration">Don't Have an Account? Sign Up!!</Link>
            </div>
            </div>
            <h4 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> NDXHardware Incorporation™
            </h4>
          </div>
        </div>
      );
    }
  }
  export default Login;
