import React, {Component} from 'react';
import { retrieveUsers, retrieveUser, createUser,updateUser,deleteUser } from "../actions/actions";
import UserForm from '../components/UserForm';
import '../css/Table.css';
class Table1 extends Component{
    constructor(){
        super();
        this.deleteRecord = this.deleteRecord.bind(this);
        this.redirectToHome = this.redirectToHome.bind(this);
        this.updateUser = this.updateUser.bind(this);
        this.fillForm = this.fillForm.bind(this);
        this.retrieveUsers = this.retrieveUsers.bind(this);
        this.retrieveUser = this.retrieveUser.bind(this);
        this.state = {
            data: []
        }
    }

    redirectToHome = () => {
        const { history } = this.props;
        if(history) history.push('/UserForm');
      } 
    componentDidMount(){
        this.retrieveUsers();
    }
    retrieveUsers = () => {
        let url = 'http://localhost:3002/users';
     fetch(url, {
        method: 'GET',
        headers: {
            'Content-Type':'application/json',
        },
     }).then((Response)=> Response.json())
     .then((ResponseJson)=> {
         this.setState({data: ResponseJson});
     }).catch(error => {
         alert(error);
     });
      }
      retrieveUser = (id) => {
        retrieveUser(id).then(res => {
          this.setState({data: res});
        });
      }
      deleteRecord = (event) => {
        deleteUser(event.target.id);
      }
      fillForm = item => event => {

        event.preventDefault();
        this.props.history.push("/UserForm", {
          account_id: item.account_id,
          user_name: item.user_name,
          user_password: item.user_password,
          user_age: item.user_age,
          user_address: item.user_address,
          user_job: item.user_job
        });
      }
      updateUser = item => event =>{
        event.preventDefault();
        this.props.history.update("/UserForm", {
          account_id: item.account_id,
          user_name: item.user_name,
          user_password: item.user_password,
          user_age: item.user_age,
          user_address: item.user_address,
          user_job: item.user_job
        });
      }

      render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='bg-secondary text-black'>
            <td>{item.account_id}</td>
            <td>{item.user_name}</td>
            <td>{item.user_password}</td>
            <td>{item.user_age}</td>
            <td>{item.user_address}</td>
            <td>{item.user_job}</td>
            <td><button onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.user_id} onClick={this.deleteRecord}>Delete</button></td>
            <td><button onClick={this.updateUser(item)}>Update</button></td>
            <td><button id={item.user_id} onClick={this.retrieveUser}>Display</button></td>
            </tr>);
          }else{
            rows.push(<tr className='bg-secondary text-green'>
            <td>{item.account_id}</td>
            <td>{item.user_name}</td>
            <td>{item.user_password}</td>
            <td>{item.user_age}</td>
            <td>{item.user_address}</td>
            <td>{item.user_job}</td>
            <td><button onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.user_id} onClick={this.deleteRecord}>Delete</button></td>
            <td><button onClick={this.updateUser(item)}>Update</button></td>
            <td><button id={item.user_id} onClick={this.retrieveUser}>Display</button></td>
            </tr>);
          }
     })
        return (
          <div className="container">
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>ID</th>
                <th>Name</th>
                <th>Password</th>
                <th>Age</th>
                <th>Address</th>
                <th>Job</th>
                <th>Edit</th>
                <th>Delete</th>
                <th>Update</th>
                <th>Display</th>
              </tr>
              </thead>
              {rows}
            </table>
          </div>
        );
      }

}
export default Table1;
