import React, { Component } from 'react';
import {retrievePCConfigurations} from '../actions/actions';
import '../App.css';
import {Link} from  'react-router-dom';
import pcsActions  from '../redux/actions/desktops';
import { connect } from 'react-redux';
class NDXSoftwareTable extends Component{
    constructor(){
        super();
        this.state = {
            TableComp:[],
        }

    }
    componentDidMount(){
        this.loadInfo();
    }
    loadInfo = () => {
        let data_array = this.props.data;
        for(let i = 0; i < data_array.length; i++){
            this.state.TableComp.push(Object.keys(data_array[i]));
        }
        this.setState({TableComp: this.state.TableComp})
    }
    render(){
        let headers = [];
        for(let i = 0; i < this.state.TableComp.length; i++){
            headers.push(<th>{this.state.TableComp[i]}</th>);
        }
        let rows = [];
        const contents = this.props.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.Proc_ID}</td>
            <td>{item.Proc_Name}</td>
            <td>{item.Proc_Freq}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectCPU(item)}>Select CPU</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
             <td>{item.Proc_ID}</td>
            <td>{item.Proc_Name}</td>
            <td>{item.Proc_Freq}</td>
            <td>{item.Price}</td>
            <td><button className="btn" id={item}  onClick={this.selectCPU(item)}>Select CPU</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                {headers}
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardwareâ„¢</b> 
            </h5>
            <br></br>
          </div>
        );
    }
}
export default NDXSoftwareTable;
