import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {retrieveCPUS} from '../actions/actions';
import { Button } from 'react-bootstrap';
class CustomPCCPU extends Component {
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
      }
      componentDidMount(){
          this.fetchCPUS();
    }
    fetchCPUS = () => {
        retrieveCPUS().then(res =>{ 
            this.setState({data:res});
        }).catch(err => {
            alert('Failed to fetch CPUS! '+err);
        });
      }
      selectCPU = item => event => {
          event.preventDefault();
          const {name} = this.props.location.state;
          let CPU = item.Proc_Name + " " + item.Proc_Freq;
          alert("Processor Selected: "+item.Proc_Name+" "+item.Proc_Freq);
          this.props.history.push("/gpu",{
              CPU:CPU,
              Price: item.Price,
              name: name
          });
      }
      render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.Proc_ID}</td>
            <td>{item.Proc_Name}</td>
            <td>{item.Proc_Freq}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectCPU(item)}>Select CPU</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
             <td>{item.Proc_ID}</td>
            <td>{item.Proc_Name}</td>
            <td>{item.Proc_Freq}</td>
            <td>{item.Price}</td>
            <td><button className="btn" id={item}  onClick={this.selectCPU(item)}>Select CPU</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a CPU Option
            </h2>
            <br></br>
            <Link className="link" to="/home" history={this.props.history} > Back to Home </Link>
            <br></br>
              </div>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>CPU NB</th>
                <th>CPU NAME</th>
                <th>CPU FREQUENCY</th>
                <th>PRICE $</th>
                <th>CPU SELECTION</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
      }
  }
  export default CustomPCCPU;
