import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {retrieveGPUS} from '../actions/actions';
import { Button } from 'react-bootstrap';
import { Logger } from 'mongodb';
class GPU extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
      }
      componentDidMount(){
          this.fetchGPUS();
    }
    fetchGPUS = () => {
        retrieveGPUS().then(res =>{ 
            this.setState({data:res});
        }).catch(err => {
            alert('Failed to fetch GPUS! '+err);
        });
      }
      selectGPU = item => event => {
        event.preventDefault();
        const {name} = this.props.location.state;
        const {CPU} = this.props.location.state;
        let GPU = item.Grap_Name+" "+item.Grap_Memory;
        const {Price} = this.props.location.state;
        let pricesum = parseFloat(item.Price) + parseFloat(Price);
        alert("Graphics Card Selected: "+item.Grap_Name+" "+item.Grap_Memory);
          this.props.history.push("/ram",{
              name:name,
              CPU:CPU,
              GPU:GPU,
              Price: Price,
              pricesum: pricesum
          });
      }
      render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.Grap_ID}</td>
            <td>{item.Grap_Name}</td>
            <td>{item.Grap_Memory}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectGPU(item)}>Select GPU</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
             <td>{item.Grap_ID}</td>
            <td>{item.Grap_Name}</td>
            <td>{item.Grap_Memory}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectGPU(item)}>Select GPU</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a GPU Option
            </h2>
            <br></br>
            <Link className="link" to={{
                pathname:'custompccpu',
                state:{}
            }}> Back to CPU Page </Link>
            <br></br>
              </div>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>GPU NB</th>
                <th>GPU NAME</th>
                <th>GPU MEMORY</th>
                <th>PRICE $</th>
                <th>GPU SELECTION</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
      }
  }
export default GPU;
