import { retrieveCurrencyRates } from '../../actions/actions';
import { toast } from 'react-toastify'


export const GET_CURRENCYRATES_STARTED = 'GET_CURRENCYRATES_STARTED'
export const GET_CURRENCYRATES_SUCCESS = 'GET_CURRENCYRATES_SUCCESS'
export const GET_CURRENCYRATES_ERROR = 'GET_CURRENCYRATES_ERROR'

const currencyActions = {

    retrieveCurrencyRates: () => async (dispatch) => {
      try {
        dispatch({
          type: GET_CURRENCYRATES_STARTED,
        });
        let response = await retrieveCurrencyRates
        dispatch({
          type: GET_CURRENCYRATES_SUCCESS,
          payload: {
            data: response,
          },
        });
      } catch (error) {
        toast.error('ERROR GETTING CURRENCY RATES' + error)
        dispatch({
          type: GET_CURRENCYRATES_ERROR,
        });
      }
    },
  };

  export default currencyActions;
