import React, {Component} from 'react';
import '../css/Table1.css';
class Table1 extends Component{
    constructor(){
        super();
        this.state = {
            data: []
        }
    }
    componentDidMount(){
        this.fetchUsers();
    }
    fetchUsers = () => {
     let url = 'http://localhost:3002/users';
     fetch(url, {
        method: 'GET',
        headers: {
            'Content-Type':'application/json',
        },
     }).then((Response)=> Response.json())
     .then((ResponseJson)=> {
         this.setState({data: ResponseJson});
     }).catch(error => {
         alert(error);
     });
    }
    render(){
        let tablerows = [];
        const tablecontents = this.state.data.forEach((item,key)=>{
            if(key%2 === 0){
                tablerows.push(<tr className='Even-Row'>
                <td>{item.account_id}</td><td>{item.user_name}</td>
                <td>{item.user_password}</td></tr>);
            }
            else{
                tablerows.push(<tr className='Odd-Row'>
                <td>{item.account_id}</td><td>{item.user_name}</td>
                <td>{item.user_password}</td></tr>);
            }
        })
        return(
           <div>
             <table className='Table'>
                 <tr className='Table-header'>
                    <th>Account ID</th>
                    <th>User Name</th>
                    <th>Password</th>
                 </tr>
                 {tablerows}
                 </table>  
           </div>
        );
    }

}
export default Table1;
