import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {retrieveRAMS} from '../actions/actions';
import { Button } from 'react-bootstrap';
class RAM extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
      }
      componentDidMount(){
          this.fetchRAMS();
    }
    fetchRAMS = () => {
        retrieveRAMS().then(res =>{ 
            this.setState({data:res});
        }).catch(err => {
            alert('Failed to fetch RAMS! '+err);
        });
      }
      selectRAM = item => event => {
          event.preventDefault();
          const {pricesum} = this.props.location.state;
          let pricesum2 = parseFloat(item.Price) + parseFloat(pricesum);
          alert("RAM Selected: "+item.RAM_Name+" "+item.RAM_Storage);
          this.props.history.push("/storage",{
              RAM_ID:item.RAM_ID,
              RAM_Name:item.RAM_Name,
              RAM_Storage:item.RAM_Storage,
              Price:item.Price,
              pricesum2:pricesum2
          });
      }
      render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.RAM_ID}</td>
            <td>{item.RAM_Name}</td>
            <td>{item.RAM_Storage}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectRAM(item)}>Select RAM</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
            <td>{item.RAM_ID}</td>
            <td>{item.RAM_Name}</td>
            <td>{item.RAM_Storage}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectRAM(item)}>Select RAM</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a RAM Option
            </h2>
            <br></br>
            <Link className="link" to="/gpu"> Back to GPU Page </Link>
            <br></br>
              </div>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>RAM NB</th>
                <th>RAM NAME</th>
                <th>RAM STORAGE</th>
                <th>PRICE $</th>
                <th>RAM SELECTION</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
      }
  }
export default RAM;
