import React, { Component } from 'react';
import {retrievePCConfigurations} from '../actions/actions';
import '../App.css';
import {Link} from  'react-router-dom';
import pcsActions  from '../redux/actions/desktops';
import { connect } from 'react-redux';
class NDXSoftwareTable extends Component{
    constructor(){
        super();
        this.state = {
            TableComp:[],
        }

    }
    componentDidMount(){
        this.loadInfo();
    }
    loadInfo = () => {
        let data_array = this.props.data[0];
            this.state.TableComp.push(Object.keys(data_array[i]));
        this.setState({TableComp: this.state.TableComp})
    }
    render(){
        let headers = [];
        console.log(this.state.TableComp[0]);
        headers.push(<th>{this.state.TableComp[0]}</th>);
        let rows = [];
        const contents = this.props.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.Proc_ID}</td>
            <td>{item.Proc_Name}</td>
            <td>{item.Proc_Freq}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn">Select CPU</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
             <td>{item.Proc_ID}</td>
            <td>{item.Proc_Name}</td>
            <td>{item.Proc_Freq}</td>
            <td>{item.Price}</td>
            <td><button className="btn" id={item}  >Select CPU</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                {headers}
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
    }
}
export default NDXSoftwareTable;
