import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {retrieveMotherboards} from '../actions/actions';
import { Button } from 'react-bootstrap';
class Motherboard extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
      }
      componentDidMount(){
          this.fetchMotherboards();
    }
    fetchMotherboards = () => {
        retrieveMotherboards().then(res =>{ 
            this.setState({data:res});
        }).catch(err => {
            alert('Failed to fetch Motherboards! '+err);
        });
      }
      selectMotherboard = (event) => {
          this.props.history.push("/psu");
      }
      render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.Moth_ID}</td>
            <td>{item.Moth_Name}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectMotherboard}>Select MB</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
          <td>{item.Moth_ID}</td>
            <td>{item.Moth_Name}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectMotherboard}>Select MB</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a Motherboard Option
            </h2>
            <br></br>
            <Link className="link" to="/storage"> Back to Storage Page </Link>
            <br></br>
              </div>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>MOTHERBOARD NB</th>
                <th>MOTHERBOARD NAME</th>
                <th>PRICE $</th>
                <th>MOTHERBOARD SELECTION</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
      }
  }
export default Motherboard;
