import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {retrieveStorages} from '../actions/actions';
import { Button } from 'react-bootstrap';
class Storages extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
      }
      componentDidMount(){
          this.fetchStorages();
    }
    fetchStorages = () => {
        retrieveStorages().then(res =>{ 
            this.setState({data:res});
        }).catch(err => {
            alert('Failed to fetch Storages! '+err);
        });
      }
      selectStorage = item => event => {
          event.preventDefault();
          const {pricesum2} = this.props.location.state;
          let pricesum3 = parseFloat(item.Price) + parseFloat(pricesum2);
          alert(pricesum3);
          this.props.history.push("/motherboard",{
              Stor_ID: item.Stor_ID,
              Stor_Name: item.Stor_Name,
              Stor_Capacity: item.Stor_Capacity,
              Price: item.Price,
              pricesum3: pricesum3
          });
      }
      render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.Stor_ID}</td>
            <td>{item.Stor_Name}</td>
            <td>{item.Stor_Capacity}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectStorage}>Select Storage</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
           <td>{item.Stor_ID}</td>
            <td>{item.Stor_Name}</td>
            <td>{item.Stor_Capacity}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectStorage}>Select Storage</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a Storage Option
            </h2>
            <br></br>
            <Link className="link" to="/ram"> Back to RAM Page </Link>
            <br></br>
              </div>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>STORAGE NB</th>
                <th>STORAGE NAME</th>
                <th>STORAGE CAPACITY</th>
                <th>PRICE $</th>
                <th>STORAGE SELECTION</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardwareâ„¢</b> 
            </h5>
            <br></br>
          </div>
        );
      }
  }
export default Storages;
