import React, { Component} from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {Slider} from '@material-ui/core/Slider';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import { Button } from 'react-bootstrap';
const PrettoSlider = withStyles({
    root: {
      color: '#5c5c5c',
      height: 8,
    },
    thumb: {
      height: 24,
      width: 24,
      backgroundColor: 'green',
      border: '2px solid green',
      marginTop: -8,
      marginLeft: -12,
      '&:focus, &:hover, &$active': {
        boxShadow: 'inherit',
      },
    },
    active: {},
    valueLabel: {
      left: 'calc(-50% + 4px)',
      color:'green',
    },
    track: {
      height: 8,
      borderRadius: 4,
      color:"green",
    },
    rail: {
      height: 8,
      borderRadius: 4,
    },
  })(Slider);
const marks = [
    {
      value: 500,
      label: '500$',
    },
    {
      value: 1250,
      label: '1250$',
    },
    {
        value: 2000,
        label: '2000$',
    }
  ];
  function valuetext(value) {
    return `${value} $`;
  }
class BudgetPC extends Component {
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: [],
        }
        this.name = "";
      }
      componentDidMount(){

    }
   gotoPCConfigurations = (event) => {
         event.preventDefault();
         let totalprice = parseFloat(this.state.SliderValue);
         const {name} = this.props.location.state;
         this.props.history.push("/pcconfigurations",{
             totalprice: totalprice,
             name:name
         });
   }
   handleChange = (event,value) => {
      this.setState({SliderValue:value});
    };
    render() {
        const {name} = this.props.location.state;
        this.name = name;
      return (
            <div className="row mt-5">
          <div className="col-lg-12 m-auto">
            <div className="card card-body">
            <h2 class="text-center mb-3" className="Label">Budget PC Configuration Page
            </h2>
            <br></br>
            <Link className="link" to={{
                pathname:'/home',
                state:{name: this.name}
            }}> Back to Home </Link>
            <br></br>
            <div className="form-group" id="over">
            <img src="greengamingpc.png" ></img>
            </div>
            <br></br>
            <div className="form-group">
            <h5 class="text-center mb-3" className="Label"><b>
            Please select a price value from the slider </b> 
            </h5>  
            <br></br>
            <br></br>
            <div className="middle">
                <div className="slider-container">
                 <PrettoSlider valueLabelDisplay="auto" onChange={this.handleChange} step={1} defaultValue={1250} min={500} max={2000} marks={marks}/>
                </div>
                <br></br>
            </div>
            <br></br>
            <br></br>
            </div>
            <div className="form-group">
                <button type="submit" onClick={this.gotoPCConfigurations} className="btn">
                 PC Results
               </button>
                </div>
            </div>
            <h5 class="text-center mb-3" className="Label"><b>
            NDXHardwareâ„¢</b> 
            </h5>
            </div> 
          </div>
      );
    }
  }

  export default BudgetPC;
