import {
    GET_CURRENCYRATES_STARTED,
    GET_CURRENCYRATES_SUCCESS,
    GET_CURRENCYRATES_ERROR
      } from "../actions/currencyrates";

      const defaultState = {
        curr_rates: [],
        isLoading: false,
      };

      function currencyRatesReducer(state = defaultState, action) {
        switch (action.type) {
          case GET_CURRENCYRATES_STARTED:
            return Object.assign({}, state, {
              isLoading: true,
            });
          case GET_CURRENCYRATES_SUCCESS:
            return Object.assign({}, state, {
              isLoading: false,
              curr_rates: action.payload.data,
            });
          case GET_CURRENCYRATES_ERROR:
            return Object.assign({}, state, {
              isLoading: false,
              curr_rates: [],
            });
          default:
            return state;
        }
      }

export default currencyRatesReducer;
