import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {findUser,retrieveUsers} from '../actions/actions';
class Login extends Component {
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.UserAuthenticationFunction = this.UserAuthenticationFunction.bind(this);
        this.state = {name: '', password: ''}

      }
      componentDidMount(){
        this.fetchUsers();
    }
    fetchUsers = () => {
      retrieveUsers().then(res =>{ 
          this.setState({data:res});
          alert('Welcome !');
      }).catch(err => {
          alert('Failed to fetch users! '+err);
      });
    }

    handleNameChange = event => {
      event.preventDefault();
      this.setState({name: event.target.value});
    }
    handlePasswordChange = event =>{
        event.preventDefault();
        this.setState({password: event.target.value});
    }

    UserAuthenticationFunction = (event) => {

      event.preventDefault();
      const {name} = this.state;
      const {password} = this.state;

      const contents = this.state.data.forEach((item,key) => {
        if(item.password != password || item.name != name){
          alert("Incorrect Username/Password");
        }
        else{
          this.props.history.push("/Table1");
        }
      });
    };
    render() {

      return (
        <div className="App">
          <h1>Login Page</h1>
          <img src="logo192.png"></img>
          <form onSubmit={this.UserAuthenticationFunction} method="POST" className="">
            <div className="form-group row">
              <label className="form-label" for="name">Name:</label>
              <input className="form-control"  type="text" onChange={this.handleNameChange} id="name" name="name" placeholder={(this.props.location.state !== undefined) ? this.props.location.state.name : 'Name'} required  size="10"/><br/>
            </div>
            <div className="form-group row">
              <label className="form-label" for="password">Password:</label>
              <input className="form-control" type="text" id="password" name="password" onChange={this.handlePasswordChange} placeholder={(this.props.location.state !== undefined) ? this.props.location.state.password : 'Password'} required  size="10"/><br/>
            </div>
            <input className="btn bg-primary text-light" type="submit" value="Login" />
            <Link to="/registration">Don't Have an Account? Sign Up!!</Link>
          </form>
        </div>
      );
    }
  }
  export default Login;
