import {
    GET_PCS_STARTED,
    GET_PCS_SUCCESS,
    GET_PCS_ERROR,
    GET_GAMERPCS_STARTED,
    GET_GAMERPCS_SUCCESS,
    GET_GAMERPCS_ERROR,
    ADD_PC_STARTED,
    ADD_PC_SUCCESS,
    ADD_PC_ERROR
       } from "../actions/desktops";

       const defaultState = {
         pcs: [],
         isLoading: false,
         didCreate: false,
        addMessage: '',
       };

       function pcReducer(state = defaultState, action) {
         switch (action.type) {
           case GET_PCS_STARTED:
             return Object.assign({}, state, {
               isLoading: true,
             });
           case GET_PCS_SUCCESS:
             return Object.assign({}, state, {
               isLoading: false,
               pcs: action.payload.data,
             });
           case GET_PCS_ERROR:
             return Object.assign({}, state, {
               isLoading: false,
               pcs: [],
             });
             case GET_GAMERPCS_STARTED:
             return Object.assign({}, state, {
               isLoading: true,
             });
           case GET_GAMERPCS_SUCCESS:
             return Object.assign({}, state, {
               isLoading: false,
               pcs: action.payload.data,
             });
           case GET_GAMERPCS_ERROR:
             return Object.assign({}, state, {
               isLoading: false,
               pcs: [],
             });
             case ADD_PC_STARTED:
              return Object.assign({},state, {
                isLoading: true,
                didCreate: false
              });
            case ADD_PC_SUCCESS:
              return Object.assign({},state, {
                isLoading: false,
                didCreate: true,
                addMessage: action.payload.message
              });
            case ADD_PC_ERROR:
              return Object.assign({}, state, {
                isLoading: false,
                didCreate: false,
                addMessage: action.payload.message,
              });
           default:
             return state;
         }
       }

 export default pcReducer;
