import {
    GET_PCS_STARTED,
    GET_PCS_SUCCESS,
    GET_PCS_ERROR
       } from "../actions/desktops";

       const defaultState = {
         pcs: [],
         isLoading: false,
       };

       function pcReducer(state = defaultState, action) {
         switch (action.type) {
           case GET_PCS_STARTED:
             return Object.assign({}, state, {
               isLoading: true,
             });
           case GET_PCS_SUCCESS:
             return Object.assign({}, state, {
               isLoading: false,
               pcs: action.payload.data,
             });
           case GET_PCS_ERROR:
             return Object.assign({}, state, {
               isLoading: false,
               pcs: [],
             });
           default:
             return state;
         }
       }

 export default pcReducer;
