import React, {Component} from 'react';
import { retrieveUsers, retrieveUser, createUser,updateUser,deleteUser } from "../actions/actions";
import UserForm from '../components/UserForm';
import '../css/Table.css';
class Table1 extends Component{
    constructor(){
        super();
        this.updateUserInfo = this.updateUser.bind(this);
        this.state = {
            data: []
        }
    }

    redirectToHome = () => {
        const { history } = this.props;
        if(history) history.push('/UserForm');
      } 
    componentDidMount(){
        this.fetchUsers();
    }
    fetchUsers = () => {
        retrieveUsers().then(res =>{ 
            this.setState({data:res});
            alert('Successfully Retrieved!');
        }).catch(err => {
            alert('Failed to fetch users! '+err);
        });
      }
      retrieveUser = (id) => {
        retrieveUser(id).then(res => {
          this.setState({data: res});
        });
      }
      deleteRecord = (event) => {
          event.preventDefault();
          let account_id = event.target.id;

        deleteUser({account_id: account_id})
        .then(response => {
            alert('Succesfully Deleted!');
            this.fetchUsers();
        }).catch(err => {
            alert('Account failed to delete !' +err);
        });
      }
      fillForm = item => event => {

        event.preventDefault();
        this.props.history.push("/UserForm", {
          account_id: item.account_id,
          user_name: item.user_name,
          user_password: item.user_password,
          user_age: item.user_age,
          user_address: item.user_address,
          user_job: item.user_job
        });
      }
      updateUserInfo = (event) =>{
        event.preventDefault();
         const{user_name} = this.state;
         const{user_password} = this.state;
         const{user_age} = this.state;
         const{user_address} = this.state;
         const{user_job} = this.state;
         let account_id = this.props.location.state.account_id;   
        updateUser({
            account_id:account_id,
            user_name:user_name,
            user_password:user_password,
            user_age:user_age,
            user_address:user_address,
            user_job:user_job,
        }).then(res => {
            this.props.history.push('/');
            alert('Account Successfully Updated');
            this.fetchUsers();
        }).catch(err =>{
            alert('Account failed to update! '+err);
        })
      }

      render() {
        let rows = [];

        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='bg-secondary text-black'>
            <td>{item.account_id}</td>
            <td>{item.user_name}</td>
            <td>{item.user_password}</td>
            <td>{item.user_age}</td>
            <td>{item.user_address}</td>
            <td>{item.user_job}</td>
            <td><button onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.account_id} onClick={this.deleteRecord}>Delete</button></td>
            <td><button onClick={this.updateUserInfo}>Update</button></td>
            <td><button id={item.account_id} onClick={this.retrieveUser}>Display</button></td>
            </tr>);
          }else{
            rows.push(<tr className='bg-secondary text-green'>
            <td>{item.account_id}</td>
            <td>{item.user_name}</td>
            <td>{item.user_password}</td>
            <td>{item.user_age}</td>
            <td>{item.user_address}</td>
            <td>{item.user_job}</td>
            <td><button onClick={this.fillForm(item)}>Edit</button></td>
            <td><button id={item.account_id} onClick={this.deleteRecord}>Delete</button></td>
            <td><button onClick={this.updateUserInfo}>Update</button></td>
            <td><button id={item.account_id} onClick={this.retrieveUser}>Display</button></td>
            </tr>);
          }
     })
        return (
          <div className="container">
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>ID</th>
                <th>Name</th>
                <th>Password</th>
                <th>Age</th>
                <th>Address</th>
                <th>Job</th>
                <th>Edit</th>
                <th>Delete</th>
                <th>Update</th>
                <th>Display</th>
              </tr>
              </thead>
              {rows}
            </table>
          </div>
        );
      }

}
export default Table1;
