import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {retrievePSUS} from '../actions/actions';
import { Button } from 'react-bootstrap';
class PSU extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
        this.pricesum4 = 0;
        this.CPU = "";
        this.GPU = "";
        this.RAM = "";
        this.Storage = "";
        this.Motherboard = "";
        this.name = "";
      }
      componentDidMount(){
          this.fetchPSUS();
    }
    fetchPSUS = () => {
        retrievePSUS().then(res =>{ 
            this.setState({data:res});
        }).catch(err => {
            alert('Failed to fetch PSUS! '+err);
        });
      }
      selectPSU = item => event => {
          event.preventDefault();
          const {name} = this.props.location.state;
          const {CPU} = this.props.location.state;
          const {GPU} = this.props.location.state;
          const {RAM} = this.props.location.state;
          const {Storage} = this.props.location.state;
          const {Motherboard} = this.props.location.state;
          let PSU = item.Pow_Name+" "+item.Pow_Capacity;
          const {pricesum3} = this.props.location.state;
        const {pricesum4} = this.props.location.state;
        let totalprice = parseFloat(item.Price) + parseFloat(pricesum4);
        alert("Power Supply Unit Selected: "+item.Pow_Name+" "+item.Pow_Capacity);
          this.props.history.push("/totalprice",{
            name:name,
            CPU:CPU,
            GPU:GPU,
            RAM:RAM,
            Storage:Storage,
            Motherboard:Motherboard,
            PSU:PSU,
            Price: item.Price,
            totalprice: totalprice
          });
      }
      render() {
        const {totalprice} = this.props.location.state;
        let rows = [];
        const {Price} = this.props.history.state;
        const {name} = this.props.location.state;
        const {CPU} = this.props.location.state;
        const {GPU} = this.props.location.state;
        const {RAM} = this.props.location.state;
        const {Storage} = this.props.location.state;
        const {Motherboard} = this.props.location.state;
        let pricesum = totalprice - Price;
        this.CPU = CPU;
        this.GPU = GPU;
        this.RAM = RAM;
        this.Storage = Storage;
        this.Motherboard = Motherboard;
        this.name = name;
        console.log(pricesum);
        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.Pow_ID}</td>
            <td>{item.Pow_Name}</td>
            <td>{item.Pow_Capacity}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectPSU(item)}>Select PSU</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
            <td>{item.Pow_ID}</td>
            <td>{item.Pow_Name}</td>
            <td>{item.Pow_Capacity}</td>
            <td>{item.Price}</td>
            <td><button id={item} className="btn" onClick={this.selectPSU(item)}>Select PSU</button></td>
            </tr>);
          };
     });
        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a PSU Option
            </h2>
            <br></br>
            <Link className="link" to={{
                pathname:'/motherboard',
                state:{name: this.name,pricesum4: pricesum,CPU:this.CPU,GPU:this.GPU,RAM:this.RAM,Storage:this.Storage,Motherboard:this.Motherboard}
            }}> Back to Motherboard Page </Link>
            <br></br>
              </div>
              <br></br>
            <div className="form-group" id="over">
            <img src="psu.png" ></img>
            </div>
            <br></br>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>PSU NB</th>
                <th>PSU NAME</th>
                <th>PSU CAPACITY</th>
                <th>PRICE $</th>
                <th>PSU SELECTION</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardwareâ„¢</b> 
            </h5>
            <br></br>
          </div>
        );
      }
  }
export default PSU;
