import React, { Component } from 'react';
import {retrievePCConfigurations} from '../actions/actions';
import '../App.css';
import {Link} from  'react-router-dom';
import pcsActions  from '../redux/actions/desktops';
import { connect } from 'react-redux';
import NDXSoftwareTable from './NDXSoftwareTable';

class PCConfigurations extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback

        this.fetchPCConfigurations = this.fetchPCConfigurations.bind(this);
        this.state = {
            data: [],
        };
        this.name ="";
      }
      componentDidMount(){
        this.fetchPCConfigurations();
    }
    async fetchPCConfigurations(){
      const {price} = this.props.location.state;
      let result = {data: price};
      let response = await this.props.dispatch(pcsActions.retrievePCConfigurations(result));
      this.setState({response});
    }

      selectPCConfiguration = item => event => {
          event.preventDefault();
          const {price} = this.props.location.state;
          const {name} = this.props.location.state;
          let Config_Name = item.Config_Name;
          let CPU = item.CPU;
          let GPU = item.GPU;
          let RAM = item.RAM;
          let Storage = item.Storage;
          let Motherboard = item.Motherboard;
          let PSU = item.PSU;
          let totalprice = item.totalprice;
          this.props.history.push("/budgetpctotalprice",{
              name: name,
              Config_Name: Config_Name,
              CPU: CPU,
              GPU: GPU,
              RAM: RAM,
              Storage: Storage,
              Motherboard: Motherboard,
              PSU: PSU,
              totalprice: totalprice,
              price: price
          });
      }
      render() {
        let rows = [];
        const {name} = this.props.location.state;
        const {totalprice} = this.props.location.state;
        this.name = name;
        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a PC Configuration Option
            </h2>
            <br></br>
            <Link className="link" to={{
                pathname: '/budgetpc',
                state: {name: this.name}
            }

            } > Back to Budget PC </Link>
            <br></br>
              </div>
              <br></br>
            <div className="form-group" id="over">
            <img src="pc.png" ></img>
            </div>
            <br></br>
            <NDXSoftwareTable data={this.props.pcs} collectionName="desktop" history={this.props.history} location={this.props.location}></NDXSoftwareTable>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
      }
}
const mapStateToProps = state =>({  
  pcs: state.pcReducer.pcs,
})

export default connect(mapStateToProps)(PCConfigurations);
