import React, { Component } from 'react';
import {retrieveCustomerOrders} from '../actions/actions';
import '../App.css';
import {Link} from  'react-router-dom';

class CustomerOrders extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: []
        };
        this.name ="";
      }
      componentDidMount(){
          this.fetchCustomerOrders();
    }
    fetchCustomerOrders = () => {
        const {name} = this.props.location.state;
        console.log(name);
        retrieveCustomerOrders({data: name}).then(res =>{ 
            this.setState({data:res});
            console.log(res);
        }).catch(err => {
            alert('Failed to fetch Your Orders! '+err);
        });
      }
      DisplayPCOrderInformation = item => event => {
          event.preventDefault();
          const {name} = this.props.location.state;
          this.props.history.push("/orderinformation",{
              name: name,
              Config_Name: item.Config_Name,
              CPU: item.CPU,
              GPU: item.GPU,
              RAM: item.RAM,
              Storage: item.Storage,
              Motherboard: item.Motherboard,
              PSU: item.PSU,
              totalprice: item.totalprice
          })
      }
      DeleteOrder = item => event =>{
        event.preventDefault();
        let CPU = item.CPU;
        deleteOrder({data: CPU})
        .then(response => {
            alert('Succesfully Deleted Order!');
            this.fetchCustomerOrders();

        }).catch(err => {
            alert('Order failed to delete !' +err);
        })
      }
      render() {
        let counter = 1;
        let rows = [];
        const {name} = this.props.location.state;
        this.name = name;
        console.log(this.state.data);
        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{counter}</td>
            <td>{item.CPU}</td>
            <td>{item.GPU}</td>
            <td>{item.RAM}</td>
            <td>{item.Storage}</td>
            <td>{item.Motherboard}</td>
            <td>{item.PSU}</td>
            <td>{item.totalprice.toFixed(2)}</td>
            <td><button id={item} className="btn" onClick={this.DisplayPCOrderInformation(item)}>Select PC</button></td>
            <td><button id={item} className="btn" onClick={this.DeleteOrder(item)}>Delete Order</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
            <td>{counter}</td>
            <td>{item.CPU}</td>
            <td>{item.GPU}</td>
            <td>{item.RAM}</td>
            <td>{item.Storage}</td>
            <td>{item.Motherboard}</td>
            <td>{item.PSU}</td>
            <td>{item.totalprice.toFixed(2)}</td>
            <td><button className="btn" id={item}  onClick={this.DisplayPCOrderInformation(item)}>Select PC </button></td>
            <td><button id={item} className="btn" onClick={this.DeleteOrder(item)}>Delete Order</button></td>
            </tr>);
          };
          counter = counter + 1;
     });

        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a PC Order Option
            </h2>
            <br></br>
            <Link className="link" to={{
                pathname: '/home',
                state: {name: this.name}
            }

            } > Back to Home </Link>
            <br></br>
              </div>
              <br></br>
            <div className="form-group" id="over">
            <img src="customerorders.png" ></img>
            </div>
            <br></br>
            <table className="table table-striped table-dark">
              <thead>
              <tr className='Table-header'>
                <th>Order NB</th>
                <th>CPU</th>
                <th>GPU</th>
                <th>RAM</th>
                <th>Storage</th>
                <th>Motherboard</th>
                <th>PSU</th>
                <th>Total Price $</th>
                <th>PC Selection</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardwareâ„¢</b> 
            </h5>
            <br></br>
          </div>
        );
      }
}
export default CustomerOrders;
