import {
   GET_RAMS_STARTED,
   GET_RAMS_SUCCESS,
   GET_RAMS_ERROR
      } from "../actions/rams";

      const defaultState = {
        rams: [],
        isLoading: false,
      };

      function ramReducer(state = defaultState, action) {
        switch (action.type) {
          case GET_RAMS_STARTED:
            return Object.assign({}, state, {
              isLoading: true,
            });
          case GET_RAMS_SUCCESS:
            return Object.assign({}, state, {
              isLoading: false,
              rams: action.payload.data,
            });
          case GET_RAMS_ERROR:
            return Object.assign({}, state, {
              isLoading: false,
              rams: [],
            });
          default:
            return state;
        }
      }

export default ramReducer;
