import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import {findUser,retrieveUsers} from '../actions/actions';
import { Button } from 'react-bootstrap';
class MainMenu extends Component {
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
      }
      componentDidMount(){
    }

    render() {

      return (
            <div className="row mt-5">
          <div className="col-lg-12 m-auto">
            <div className="card card-body">
            <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i>Welcome to NDXHardware™
            </h2>
            <br></br>
            <div className="form-group" id="over">
            <img src="greengamingpc.png" ></img>
            </div>
            <div className="form-group">
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            Please select an option to satisfy your need for pc gaming:</b> 
            </h5>  
            </div>
            <div className="form-group">
                <div className="form-group">
                <button type="submit" onClick={this.UserAuthenticationFunction} className="btn">
                 Budget PC
               </button>
                </div>
                <div className="form-group">
                <button type="submit" onClick={this.UserAuthenticationFunction} className="btn">
                 Custom PC
               </button>
                </div>
            </div>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            </div> 
          </div>
        </div>
      );
    }
  }
  export default MainMenu;
