import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';
import { Button, Navbar, Nav, NavItem, NavDropdown, MenuItem } from 'react-bootstrap';
import {retrieveCurrencyRates} from '../actions/actions';
import customerordersActions from "../redux/actions/customerorders";
import { connect } from "react-redux";
import NDXSoftwareTable from './NDXSoftwareTable';
import { toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css';
class CurrencyConverter extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: [],
            amount:''
        };
        this.name = "";
        this.rate = 0.0;
      }
      componentDidMount(){

    }
    handleRateChange = (event) => {
        event.preventDefault();
        this.setState({amount: event.target.value});
      }
    gotoHome = (event) => {
        event.preventDefault();
        const {name} = this.props.location.state;
        this.props.history.push("/home",{
            name:name
        }); 
    }
    Logout = (event) => {
      event.preventDefault();
      const {name} = this.props.location.state;
      this.props.history.push("/",{
          name:name
      });
    }
    gotoCustomerOrders = (event) => {
      event.preventDefault();
      const {name} = this.props.location.state;
      this.props.history.push("/customerorders",{
          name:name
      });
    }
    gotoCurrencyRates = (event) => {
        event.preventDefault();
        const {name} = this.props.location.state;
        this.props.history.push("/currencyrates",{
            name:name
        });
      }
    convertCurrency = (event) => {
        event.preventDefault();
        const {name} = this.props.location.state;
        const {rate} = this.props.location.state;
        const {amount} = this.state;
        let converted_amount = (parseFloat(rate.toFixed(2)))*(parseFloat(amount));
        toast.success("The amount converted from USD to the selected rate is: "+converted_amount);

      }
      render() {
        const {name} = this.props.location.state;
        console.log(name);
        return (
          <div className="container">
              <br></br>
              <Navbar fixed="top" className="color-nav" collapseOnSelect expand="lg" variant="dark" >
          <Navbar.Brand onClick={this.gotoHome}>
      <img
        alt=""
        src="/greengamingpc.png"
        width="25"
        height="33"
        className="d-inline-block align-top"
      />{' '}
     NDXHardware™
    </Navbar.Brand>
  <Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <Navbar.Collapse id="responsive-navbar-nav">
    <Nav className="mr-auto">
      <Nav.Link onClick={this.gotoCustomerOrders}>Customer Orders</Nav.Link>
      <Nav.Link onClick={this.gotoCurrencyRates}>Currency Rates</Nav.Link>
    </Nav>
    <Nav>
      <Nav.Link onClick={this.Logout}>
        Logout 
      </Nav.Link>
    </Nav>
  </Navbar.Collapse>
</Navbar>
              <br></br> 
              <br></br>
              <br></br>
              <br></br> 
              <br></br>
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Currency Conversion from USD to your Selected Currency
            </h2>
            <br></br>
              </div>
              <br></br>

            <div className="form-group" id="over">
            <img src="cash.png" ></img>
            </div>
            <br></br>
            <div className="form-group">
              <h3 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please enter a value in USD to be converted for the selected currency:
            </h3>
            <br></br>
              </div>
              <br></br>
            <div className="form-group">   
          <label className="Label" for="amount">Amount in $:</label>
          <input class="form-control"  type="amount" onChange={this.handleRateChange} id="amount" name="amount" placeholder={(this.props.location.state !== undefined) ? this.props.location.state.amount : 'Amount'} required  size="10"/>    
          <br/>
          </div>
            <br></br>
            <br></br>
            <button type="submit" onClick={this.convertCurrency} className="btn">
            Convert Rate
          </button>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
      }
  }

  export default CurrencyConverter;
