import React, { Component } from 'react';
import {retrievePCConfigurations} from '../actions/actions';
import '../App.css';
import {Link} from  'react-router-dom';
import pcsActions  from '../redux/actions/desktops';
import { connect } from 'react-redux';
class NDXSoftwareDropDown extends Component{
    constructor(){
        super();
        this.state = {
            DropDownComp:[],
        }

    }
    componentDidMount(){
        this.loadInfo();
    }
    loadInfo = () => {
        console.log(this.props.data);
        let data_array = this.props.data;
        let DropDownComp = [];
        console.log(data_array)
        for(let i = 0; i < data_array.length; i++){
            let item = data_array[i];
            DropDownComp.push(<option style={{backgroundColor:'black',color:'green'}} key={i} value={item.id}>{item.name}</option>);

        }
        this.setState({DropDownComp:DropDownComp});
    }

    render(){
        return(
        <div className="form-group">
        <select style={{backgroundColor:'black',color:'green'}}>{this.state.DropDownComp}</select>
        </div>
        );
    }
}
export default NDXSoftwareDropDown;
