import React, { Component } from 'react';
import '../App.css';
import {Link} from  'react-router-dom';


class UserForm extends Component {

  constructor(props){
    super(props);
    // This binding is necessary to make `this` work in the callback
    this.UserRegistrationFunction = this.UserRegistrationFunction.bind(this);
    this.state = {Username: '', Password: '', Age: '', Address: '', Profession: ''}
  }

  componentDidMount(){

  }

  handleNameChange = event => {
    event.preventDefault();
    this.setState({Username: event.target.value});
  }
  handlePasswordChange = event =>{
      event.preventDefault();
      this.setState({Password: event.target.value});
  }

  handleAgeChange = event => {
    event.preventDefault();
    this.setState({Age: event.target.value});
  }

  handleAddress = event => {
    event.preventDefault();
    this.setState({Address: event.target.value});
  }

  handleJob = event => {
    event.preventDefault();
    this.setState({Profession: event.target.value});
  }

  UserRegistrationFunction = (event) => {

    event.preventDefault();
    const {Username} = this.state;
    const {Age} = this.state;
    const {Address} = this.state;
    const {Profession} = this.state;
    let userID = '';

    let url = '';

    if(this.props.location.state === undefined)
    {
      url = 'http://localhost:3002/addUser';
    }else{
      userID = this.props.location.state.user_id;
      url = 'http://localhost:3002/updateUser';
    }

    fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: Username,
          age: Age,
          address: Address,
          job: Profession,
          user_id: userID,
        })
}).then((response) => response.json())
      .then((responseJson) => {

        alert(responseJson.value);
        this.props.history.push('/');

      }).catch((error) => {
        alert('Failed to Save.');
      });
  }
  render() {

    return (
      <div className="App">
        <h1>User Account Form</h1>
        <Link to="/"> Go back to Table </Link>
        <form onSubmit={this.UserRegistrationFunction} method="POST" className="">
          <div className="form-group row">
            <label className="form-label" for="name">Name:</label>
            <input className="form-control"  type="text" onChange={this.handleNameChange} id="name" name="name" placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_name : 'Name'} required  size="10"/><br/>
          </div>
          <div className="form-group row">
            <label className="form-label" for="age">Password:</label>
            <input className="form-control" type="text" id="age" name="age" onChange={this.handleAgeChange} placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_age : 'Age'} required  size="10"/><br/>
          </div>
          <div className="form-group row">
            <label className="form-label" for="age">Age:</label>
            <input className="form-control" type="text" id="age" name="age" onChange={this.handleAgeChange} placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_age : 'Age'} required  size="10"/><br/>
          </div>
        <div className="form-group row">
          <label className="form-label" for="occupation">Address:</label>
          <input className="form-control" type="text" id="occupation" name="occupation" placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_occupation : 'Occupation'} onChange={this.handleOccupation} required  size="10"/><br/>
        </div>
        <div className="form-group row">
          <label className="form-label" for="hobby">Job:</label>
          <input className="form-control" type="text" id="hobby" name="hobby" placeholder={(this.props.location.state !== undefined) ? this.props.location.state.user_hobby : 'Hobby'} required size="10" onChange={this.handleHobby}/><br/>
        </div>
          <input className="btn bg-primary text-light" type="submit" value="Save" />
        </form>
      </div>
    );
  }
}

export default UserForm;
