import React, { Component, useEffect } from 'react';
import {retrievePCConfigurations} from '../actions/actions';
import '../App.css';
import {Link} from  'react-router-dom';

class PCConfigurations extends Component{
    constructor(props){
        super(props);
        // This binding is necessary to make `this` work in the callback
        this.state = {
            data: [],
        };
        this.name ="";
      }
      componentDidMount(){
          this.fetchPCConfigurations();
    }
    fetchPCConfigurations = () => {
        const {totalprice} = this.props.location.state;
        console.log(totalprice);
        retrievePCConfigurations({data: totalprice}).then(res =>{ 
            this.setState({data:res});
            console.log(res);
        }).catch(err => {
            alert('Failed to fetch PCConfigurations! '+err);
        });
      }
      selectPCConfiguration = item => event => {
          event.preventDefault();
          const {name} = this.props.location.state;
          let CPU = item.Proc_Name + " " + item.Proc_Freq;
          alert("Processor Selected: "+item.Proc_Name+" "+item.Proc_Freq);
          this.props.history.push("/gpu",{
              CPU:CPU,
              Price: item.Price,
              name: name
          });
      }
      render() {
        let rows = [];
        const {name} = this.props.location.state;
        this.name = name;
        const contents = this.state.data.forEach((item, key) => {
          if(key % 2 === 0)
          {
            rows.push(<tr className='Odd-Row'>
            <td>{item.Config_Name}</td>
            <td>{item.CPU}</td>
            <td>{item.GPU}</td>
            <td>{item.RAM}</td>
            <td>{item.Storage}</td>
            <td>{item.Motherboard}</td>
            <td>{item.PSU}</td>
            <td>{item.totalprice}</td>
            <td><button id={item} className="btn" onClick={this.selectPCConfiguration(item)}>Select PC</button></td>
            </tr>);
          }else{
            rows.push(<tr className='Even-Row'>
            <td>{item.Config_Name}</td>
            <td>{item.CPU}</td>
            <td>{item.GPU}</td>
            <td>{item.RAM}</td>
            <td>{item.Storage}</td>
            <td>{item.Motherboard}</td>
            <td>{item.PSU}</td>
            <td>{item.totalprice}</td>
            <td><button className="btn" id={item}  onClick={this.selectPCConfiguration(item)}>Select PC </button></td>
            </tr>);
          };
     });

        return (
          <div className="container">
              <br></br>
              <div className="form-group">
              <h2 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i> Please Select a PC Configuration Option
            </h2>
            <br></br>
            <Link className="link" to={{
                pathname: '/home',
                state: {name: this.name}
            }

            } > Back to Budget PC </Link>
            <br></br>
              </div>
            <table className="table table-striped">
              <thead>
              <tr className='Table-header'>
                <th>Configuration Name</th>
                <th>CPU</th>
                <th>GPU</th>
                <th>RAM</th>
                <th>Storage</th>
                <th>Motherboard</th>
                <th>PSU</th>
                <th>Total Price $</th>
              </tr>
              </thead>
              {rows}
            </table>
            <br></br>
            <h5 class="text-center mb-3" className="Label"><i class="fas fa-sign-in-alt"></i><b>
            NDXHardware™</b> 
            </h5>
            <br></br>
          </div>
        );
      }
}
export default PCConfigurations;
